const initialPost = {
    posts:[],
    loading:false,
    error:false
}

const postReducer = (state=initialPost,action)=>{
    switch (action.type) {
        case "UPLOAD_START":
            return{
                ...state,
                uploading:true,
                error:false
            };

        case "UPLOAD_SUCCESS":
            return{
                ...state,
                uploading:false,
                posts:[action.data,...state.posts]
            };

        case "UPLOAD_FAIL":
        return{
            ...state,
            uploading:false,
            error:true
        };
    
        default:
            return state;
    }
}

export default postReducer;
