import React, { useState } from "react";
import "./Auth.css";
import Logo from "../../img/logo.png";
import { useDispatch } from "react-redux";;

const Auth = () => {
  const [isSignUp,setIsSignUp] = useState(false);
  const dispatch = useDispatch();
  const [data,setData] = useState({
    firstname:"",
    lastname:"",
    password:"",
    username:"",
    confirmpass:''
  });

  const [confirmPass,setConfirmPass] = useState(true)

  const handleChange = (e) =>{
    setData({...data,[e.target.name] : e.target.value})
  }

  const handleSubmit = (e) =>{
    e.preventDefault();
    if(isSignUp){
      data.password === data.confirmpass  
      ? dispatch(signup(data))
      : dispatch(setConfirmPass(false))
    }
  }

  const resetForm = () =>{
    setConfirmPass(true);
    setData({
      firstname:"",
      lastname:"",
      password:"",
      username:"",
      confirmpass:''
    })
  }

  return (
    <div className="Auth">
      {/* left side */}
      <div className="a-left">
        <img src={Logo} alt="" />
        <div className="Webname">
          <h1>ZKC Media</h1>
          <h6>Explore the ideas throughout the world</h6>
        </div>
      </div>

       {/* right side */}
      <div className="a-right">
      <form className="infoForm authForm" onSubmit={handleSubmit}>
        <h3>{isSignUp ? "Sign up" : "Log in"}</h3>

        {isSignUp &&
          <div>
            <input
              type="text"
              placeholder="First Name"
              className="infoInput"
              name="firstname"
              onChange={handleChange}
              value={data.firstname}
            />
            <input
              type="text"
              placeholder="Last Name"
              className="infoInput"
              name="lastname"
              onChange={handleChange}
              value={data.lastname}
            />
          </div>
        }
        
        <div>
          <input
            type="text"
            className="infoInput"
            name="username"
            placeholder="Usernames"
            onChange={handleChange}
            value={data.username}
          />
        </div>

        <div>
          <input
            type="password"
            className="infoInput"
            name="password"
            placeholder="Password"
            onChange={handleChange}
            value={data.password}
          />
          {isSignUp && 
            <input
              type="password"
              className="infoInput"
              name="confirmpass"
              placeholder="Confirm Password"
              onChange={handleChange}
              value={data.confirmpass}
            />
          }
         
        </div>
          <span style={{display:confirmPass ? "none" : "block",color:"red",fontSize:"12px",alignSelf:"flex-end",marginRight:"5px"}}>* Confirm Password is not same</span>
        <div>
            <span onClick={()=>{setIsSignUp((prev)=>!prev);resetForm()}} style={{fontSize: '12px',cursor:"pointer"}}>{isSignUp ? "Already have an account. Login!" : "Don't have an account? Signup"}</span>
        </div>
        <button className="button infoButton" type="submit">{isSignUp ? "Signup" :"Login"}</button>
      </form>
    </div>
    </div>
  );
};



export default Auth;
