import {AUTH_FAIL,AUTH_START,AUTH_SUCCESS} from "../actions/AuthAction.js"

const initialState = {
    authData:null,
    loading:false,
    error:false
}

const AuthReducer = (state=initialState,action) =>{
    switch (action.type) {
        case AUTH_START:
            return{
                ...state,
                loading:true,
                error:false
            };

        case AUTH_SUCCESS:
        localStorage.setItem("profile",JSON.stringify({...action?.data}));
        return{
            ...state,
            loading:false,
            authData:action.data,
            error:false
        };

        case AUTH_FAIL:
        return{
            ...state,
            loading:false,
            error:true
        };
    
        default:
           return state;
    }
}

export default AuthReducer;
