import { Modal, useMantineTheme } from "@mantine/core";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { UploadImage } from "../../actions/UploadAction";
import { updateUser } from "../../Api/UserRequest";

function ProfileModal({ modalOpened, setModalOpened,data }) {
  const theme = useMantineTheme();

  const {password,...other} = data;
  const [formData,setFormData] = useState(other);
  const [profileImage,setProfileImage] = useState(null);
  const [coverImage,setCoverImage] = useState(null);
  const dispatch = useDispatch();
  const params = useParams();
  const {user} = useSelector((state)=>state.AuthReducer.authData)

  const handleChange = (e) =>{
    setFormData({
      ...formData,
      [e.target.name] : e.target.value
    })
  }
  const changeImageHandle = (e) =>{
    if(e.target.files && e.target.files[0]){
      let img = e.target.files[0];
      e.target.name === "profileImage" 
      ? setProfileImage(img) : setCoverImage(img)
    }
  }
  const submitHandle = (e) =>{
    e.preventDefault();
    let userData = formData;
    if(profileImage){
      const data = new formData();
      const fileName = Date.now() + profileImage.name;
      data.append("name",fileName);
      data.append("file",profileImage);
      userData.profilePicture = fileName;

      try {
        dispatch(UploadImage(data))
      } catch (error) {
        console.log(error)
      }
    }

    if(coverImage){
      const data = new formData();
      const fileName = Date.now() + coverImage.name;
      data.append("name",coverImage);
      data.append("file",profileImage);
      userData.coverPicture = fileName;

      try {
        dispatch(UploadImage(data))
      } catch (error) {
        console.log(error)
      }
    }
    dispatch(updateUser(params.id),userData)
    setModalOpened(false)
  }
  return (
    <Modal
      overlayColor={
        theme.colorScheme === "dark"
          ? theme.colors.dark[9]
          : theme.colors.gray[2]
      }
      overlayOpacity={0.55}
      overlayBlur={3}
      size="55%"
      opened={modalOpened}
      onClose={() => setModalOpened(false)}
    >
      <form className="infoForm" onSubmit={submitHandle}>
        <h3>Your info</h3>

        <div>
          <input
            type="text"
            className="infoInput"
            name="firstname"
            placeholder="First Name"
            onChange={handleChange}
            value={formData.firstname}
          />

          <input
            type="text"
            className="infoInput"
            name="lastname"
            placeholder="Last Name"
            onChange={handleChange}
            value={formData.lastname}
          />
        </div>

        <div>
          <input
            type="text"
            className="infoInput"
            name="worksAt"
            placeholder="Works at"
            onChange={handleChange}
            value={formData.worksAt}
          />
        </div>

        <div>
          <input
            type="text"
            className="infoInput"
            name="livesin"
            placeholder="LIves in"
            onChange={handleChange}
            value={formData.livesin}
          />

          <input
            type="text"
            className="infoInput"
            name="country"
            placeholder="Country"
            onChange={handleChange}
            value={formData.country}
          />
        </div>

        <div>
          <input
            name="relationship"
            type="text"
            className="infoInput"
            placeholder="RelationShip Status"
            onChange={handleChange}
            value={formData.relationship}
          />
        </div>


        <div>
            Profile Image 
            <input type="file" name='profilePicture'  onChange={changeImageHandle}/>
            Cover Image
            <input type="file" name="coverPicture"  onChange={changeImageHandle} />
        </div>

        <button className="button infoButton">Update</button>
      </form>
    </Modal>
  );
}

export default ProfileModal;
