import React from "react";
import { useSelector } from "react-redux";
import Cover from "../../img/cover.jpg";
import Profile from "../../img/profileImg.jpg";
import "./ProfileCard.css";
import {Link} from 'react-router-dom'

const ProfileCard = ({location}) => {
  const user = useSelector((state)=>state.AuthReducer.authData);
  const serverPublic = process.env.REACT_APP_PUBLIC_FOLDER;

  return (
    <div className="ProfileCard">
      <div className="ProfileImages">
        <img src={user.newUser.coverPicture ? serverPublic + user.newUser.coverPicture : serverPublic + "default.png"} alt="" />
        <img src={user.newUser.profilePicture ? serverPublic + user.newUser.profilePicture : serverPublic + "default-profile.png"} alt="" />
      </div>

      <div className="ProfileName">
        <span>{user.newUser.firstname} {user.newUser.lastname}</span>
        <span>{user.newUser.worksAt ? user.newUser.worksAt : "Write Your About Self."}</span>
      </div>

      <div className="followStatus">
        <hr />
        <div>
          <div className="follow">
            <span>{user.newUser.following.length}</span>
            <span>Followings</span>
          </div>
          <div className="vl"></div>
          <div className="follow">
            <span>{user.newUser.followers.length}</span>
            <span>Followers</span>
          </div>

          {location === ProfilePage && (
            <>
              <div className="vl"></div>
              <div className="follow">
                <span>3</span>
                <span>Posts</span>
              </div>
            </>
          )}
        </div>
        <hr />
      </div>
      {location === ProfilePage ? "" : <span>
          <Link style={{textDecoration:"none",color:"inherit"}} to={`/profile/${user.newUser._id}`}>My Profile</Link>
         </span>}
    </div>
  );
};

export default ProfileCard;
